import React from 'react';
import '../App.css';
import ChannelStats from './ChannelStats';

function Headline() {
  const m7 = useYouTubeStats(M7_CHANNEL_ID, API_KEY);
  const m7cs = useYouTubeStats(M7CS_CHANNEL_ID, API_KEY);
  return (
    <div className="headline-container">
      <h1 className="headline">
        OUR THIS YEAR GOAL <span className="gradient-text">create content.</span><br />
        {/* We create the <span className="gradient-text">future of YouTube.</span> */}
      </h1>
      {/* {<div className="two-col-stats"> */}
          <ChannelStats
            title="M7"
            // subscriberCount={m7.subscriberCount}
            viewCount={m7.viewCount}
            error={m7.error}
          />
          <ChannelStats
            title="M7CS"
            // subscriberCount={m7cs.subscriberCount}
            viewCount={m7cs.viewCount}
            error={m7cs.error}
          />
        {/* </div>} */}
    </div>
  );
}

export default Headline;
