import React, { useEffect, useState } from 'react';
import './App.css';

const M7_CHANNEL_ID = process.env.REACT_APP_M7_CHANNEL_ID;
const M7CS_CHANNEL_ID = process.env.REACT_APP_M7CS_CHANNEL_ID;
const API_KEY = process.env.REACT_APP_YT_API_KEY;

function useYouTubeStats(channelId, apiKey) {
  const [subscriberCount, setSubscriberCount] = useState(null);
  const [viewCount, setViewCount] = useState(null);
  const [liveViewers, setLiveViewers] = useState(null);
  const [isLive, setIsLive] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!channelId || !apiKey) return;
    async function fetchChannelStats() {
      try {
        // Fetch channel stats
        const response = await fetch(
          `https://www.googleapis.com/youtube/v3/channels?part=statistics&id=${channelId}&key=${apiKey}`
        );
        const data = await response.json();
        if (data.items && data.items.length > 0) {
          setSubscriberCount(data.items[0].statistics.subscriberCount);
          setViewCount(data.items[0].statistics.viewCount);
        } else {
          setError('Channel not found or API error.');
        }
        // Fetch live broadcast
        const liveResp = await fetch(
          `https://www.googleapis.com/youtube/v3/search?part=id&channelId=${channelId}&eventType=live&type=video&key=${apiKey}`
        );
        const liveData = await liveResp.json();
        if (liveData.items && liveData.items.length > 0) {
          setIsLive(true);
          const liveVideoId = liveData.items[0].id.videoId;
          // Fetch live viewers
          const videoResp = await fetch(
            `https://www.googleapis.com/youtube/v3/videos?part=liveStreamingDetails&id=${liveVideoId}&key=${apiKey}`
          );
          const videoData = await videoResp.json();
          if (
            videoData.items &&
            videoData.items.length > 0 &&
            videoData.items[0].liveStreamingDetails &&
            videoData.items[0].liveStreamingDetails.concurrentViewers
          ) {
            setLiveViewers(videoData.items[0].liveStreamingDetails.concurrentViewers);
          } else {
            setLiveViewers('0');
          }
        } else {
          setIsLive(false);
          setLiveViewers(null);
        }
      } catch (err) {
        setError('Failed to fetch channel statistics.');
      }
    }
    fetchChannelStats();
    const interval = setInterval(fetchChannelStats, 1000);
    return () => clearInterval(interval);
  }, [channelId, apiKey]);

  return { subscriberCount, viewCount, liveViewers, isLive, error };
}

function App() {
  const m7 = useYouTubeStats(M7_CHANNEL_ID, API_KEY);
  const m7cs = useYouTubeStats(M7CS_CHANNEL_ID, API_KEY);

  return (
    <div className="main-bg">
      <nav className="navbar">
        <div className="logo"><span className="logo-nb">NB</span><span className="logo-media">Media</span></div>
        <ul className="nav-links">
          <li><i className="fa-brands fa-instagram" /></li>
          <li><i className="fa-brands fa-linkedin-in" /></li>
        </ul>
      </nav>
      <main className="center-content">
        <div className="headline-container">
          <h1 className="headline">
            We don't just <span className="gradient-text">create content.</span><br />
            We create the <span className="gradient-text">future of YouTube.</span>
          </h1>
          <p className="subtitle">
            NB Media is the undisputed leader in YouTube content creation with a vibrant, Gen Z-friendly culture.
          </p>
        </div>
        <div className="two-col-stats">
          <div className="channel-col">
            <h2>M7</h2>
            {m7.subscriberCount !== null && m7.viewCount !== null ? (
              <>
                <div className="stat">Subscribers: <span>{m7.subscriberCount}</span></div>
                <br />
                <div className="stat">Views: <span>{m7.viewCount}</span></div>
                <br />
                <div className="stat">Live Viewers: <span>{m7.isLive ? m7.liveViewers : 'Not live'}</span></div>
              </>
            ) : m7.error ? (
              <div className="stat error">{m7.error}</div>
            ) : (
              <div className="stat">Loading stats...</div>
            )}
          </div>
          <div className="channel-col">
            <h2>M7CS</h2>
            {m7cs.subscriberCount !== null && m7cs.viewCount !== null ? (
              <>
                <div className="stat">Subscribers: <span>{m7cs.subscriberCount}</span></div>
                <div className="stat">Views: <span>{m7cs.viewCount}</span></div>
                <div className="stat">Live Viewers: <span>{m7cs.isLive ? m7cs.liveViewers : 'Not live'}</span></div>
              </>
            ) : m7cs.error ? (
              <div className="stat error">{m7cs.error}</div>
            ) : (
              <div className="stat">Loading stats...</div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
